use std::str::FromStr;

use rsacracker::{integer_to_string, run_attacks, Parameters};
use rug::Integer;

#[test]
fn fcsc_2024_adversarial_crypto() {
    // From FCSC 2024 / AdveRSArial Crypto
    // https://hackropole.fr/fr/challenges/crypto/fcsc2024-crypto-adversarial-crypto-1/

    let params = Parameters {
        e: 65537.into(),
        n : Some(Integer::from_str("").unwrap()),
        c : Some(Integer::from_str("").unwrap()),
        ..Default::default()

    };

    let solution = run_attacks(&params).unwrap();
    assert!(solution.pk.is_none());
    assert_eq!(
        integer_to_string(&solution.m.unwrap()).unwrap().trim(),
        "FCSC{d0bf88291bcd488f28a809c9ae79d53da9caefc85b3790f57615e61c70a45f3c}"
    );
}
