use std::str::FromStr;

use rsacracker::{integer_to_string, run_attacks, Parameters};
use rug::Integer;

#[test]
fn picoctf_2022_sum_o_primes() {
    // From picoCTF 2022 / Sum-O-Primes
    // https://ctftime.org/task/19785

    let params = Parameters {
        n: Some(Integer::from_str("").unwrap()),
        sum_pq: Some(Integer::from_str("").unwrap()),
        c: Some(Integer::from_str("").unwrap()),
        ..Default::default()
    };

    let (private_key, m) = run_attacks(&params).unwrap();
    assert!(private_key.is_some());
    assert_eq!(
        integer_to_string(&m.unwrap()).unwrap().trim(),
        "picoCTF{24929c45}"
    );
}
